import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import Navbar from '../components/Navbar';

const Profile = () => {
  const { user, updateProfile, loading, error, clearError } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: user?.firstName || '',
    lastName: user?.lastName || '',
    firstNameEn: user?.firstNameEn || '',
    lastNameEn: user?.lastNameEn || '',
    phone: user?.phone || '',
    city: user?.city || '',
    storeName: user?.storeName || '',
    storeNameEn: user?.storeNameEn || '',
    storeDescription: user?.storeDescription || ''
  });
  const [successMessage, setSuccessMessage] = useState('');

  const saudiCities = [
    'Riyadh', 'Jeddah', 'Makkah', 'Madinah', 'Dammam', 
    'Khobar', 'Dhahran', 'Taif', 'Tabuk', 'Abha',
    'Qassim', 'Hail', 'Najran', 'Jazan'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    clearError();
    setSuccessMessage('');

    const result = await updateProfile(formData);
    
    if (result.success) {
      setSuccessMessage('ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠');
      setIsEditing(false);
    }
  };

  const handleCancel = () => {
    setFormData({
      firstName: user?.firstName || '',
      lastName: user?.lastName || '',
      firstNameEn: user?.firstNameEn || '',
      lastNameEn: user?.lastNameEn || '',
      phone: user?.phone || '',
      city: user?.city || '',
      storeName: user?.storeName || '',
      storeNameEn: user?.storeNameEn || '',
      storeDescription: user?.storeDescription || ''
    });
    setIsEditing(false);
    clearError();
  };

  return (
    <>
      <Navbar />
      <div className="page">
        <div className="container" style={{ maxWidth: '800px' }}>
          {/* Header */}
          <div className="dashboard-header">
            <h1 className="dashboard-title">ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä</h1>
            <p className="dashboard-subtitle">ÿ•ÿØÿßÿ±ÿ© ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ≠ÿ≥ÿßÿ®ŸÉ</p>
          </div>

          {/* Success Message */}
          {successMessage && (
            <div className="alert alert-success">
              <span>‚úì</span>
              <span>{successMessage}</span>
            </div>
          )}

          {/* Error Message */}
          {error && (
            <div className="alert alert-error">
              <span>‚ö†Ô∏è</span>
              <span>{error}</span>
            </div>
          )}

          {/* Profile Card */}
          <div className="card">
            <div className="card-header" style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center' 
            }}>
              <h3>ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ¥ÿÆÿµŸäÿ©</h3>
              {!isEditing && (
                <button 
                  className="btn btn-secondary btn-sm"
                  onClick={() => setIsEditing(true)}
                >
                  ‚úèÔ∏è ÿ™ÿπÿØŸäŸÑ
                </button>
              )}
            </div>

            <div className="card-body">
              {/* Profile Picture Section */}
              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: 'var(--space-lg)',
                marginBottom: 'var(--space-xl)',
                paddingBottom: 'var(--space-xl)',
                borderBottom: '1px solid var(--gray-100)'
              }}>
                <div style={{
                  width: '100px',
                  height: '100px',
                  borderRadius: '50%',
                  background: 'linear-gradient(135deg, var(--primary) 0%, var(--primary-dark) 100%)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  color: 'var(--white)',
                  fontSize: '2.5rem',
                  fontWeight: '700'
                }}>
                  {user?.firstName?.[0] || 'üë§'}
                </div>
                <div>
                  <h3 style={{ marginBottom: 'var(--space-xs)' }}>
                    {user?.firstName} {user?.lastName}
                  </h3>
                  <p style={{ color: 'var(--gray-500)', marginBottom: 'var(--space-sm)' }}>
                    {user?.email}
                  </p>
                  <span style={{
                    display: 'inline-block',
                    padding: '4px 12px',
                    background: user?.role === 'seller' ? 'var(--accent-lighter)' : 'var(--primary-lighter)',
                    color: user?.role === 'seller' ? 'var(--accent-dark)' : 'var(--primary-dark)',
                    borderRadius: 'var(--radius-full)',
                    fontSize: '0.875rem',
                    fontWeight: '600'
                  }}>
                    {user?.role === 'seller' ? 'üè™ ÿ®ÿßÿ¶ÿπ' : 'üõí ŸÖÿ¥ÿ™ÿ±Ÿä'}
                  </span>
                </div>
              </div>

              {/* Profile Form */}
              <form onSubmit={handleSubmit}>
                {/* Name Fields */}
                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: '1fr 1fr', 
                  gap: 'var(--space-md)' 
                }}>
                  <div className="form-group">
                    <label className="form-label">ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      disabled={!isEditing}
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">ÿßÿ≥ŸÖ ÿßŸÑÿπÿßÿ¶ŸÑÿ©</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      disabled={!isEditing}
                    />
                  </div>
                </div>

                {/* English Name Fields */}
                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: '1fr 1fr', 
                  gap: 'var(--space-md)' 
                }}>
                  <div className="form-group">
                    <label className="form-label">First Name (English)</label>
                    <input
                      type="text"
                      name="firstNameEn"
                      className="form-input"
                      value={formData.firstNameEn}
                      onChange={handleChange}
                      disabled={!isEditing}
                      dir="ltr"
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Last Name (English)</label>
                    <input
                      type="text"
                      name="lastNameEn"
                      className="form-input"
                      value={formData.lastNameEn}
                      onChange={handleChange}
                      disabled={!isEditing}
                      dir="ltr"
                    />
                  </div>
                </div>

                {/* Email (Read-only) */}
                <div className="form-group">
                  <label className="form-label">ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä</label>
                  <input
                    type="email"
                    className="form-input"
                    value={user?.email || ''}
                    disabled
                    dir="ltr"
                    style={{ background: 'var(--gray-50)' }}
                  />
                  <span className="form-hint">ŸÑÿß ŸäŸÖŸÉŸÜ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä</span>
                </div>

                {/* Phone */}
                <div className="form-group">
                  <label className="form-label">ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ</label>
                  <input
                    type="tel"
                    name="phone"
                    className="form-input"
                    value={formData.phone}
                    onChange={handleChange}
                    disabled={!isEditing}
                    dir="ltr"
                  />
                </div>

                {/* City */}
                <div className="form-group">
                  <label className="form-label">ÿßŸÑŸÖÿØŸäŸÜÿ©</label>
                  <select
                    name="city"
                    className="form-select"
                    value={formData.city}
                    onChange={handleChange}
                    disabled={!isEditing}
                  >
                    <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÖÿØŸäŸÜÿ©</option>
                    {saudiCities.map(city => (
                      <option key={city} value={city}>{city}</option>
                    ))}
                  </select>
                </div>

                {/* Seller-specific fields */}
                {user?.role === 'seller' && (
                  <>
                    <div style={{ 
                      margin: 'var(--space-xl) 0',
                      padding: 'var(--space-lg) 0',
                      borderTop: '1px solid var(--gray-100)',
                      borderBottom: '1px solid var(--gray-100)'
                    }}>
                      <h4 style={{ marginBottom: 'var(--space-lg)' }}>ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÖÿ™ÿ¨ÿ±</h4>

                      <div className="form-group">
                        <label className="form-label">ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ™ÿ¨ÿ± (ÿπÿ±ÿ®Ÿä)</label>
                        <input
                          type="text"
                          name="storeName"
                          className="form-input"
                          value={formData.storeName}
                          onChange={handleChange}
                          disabled={!isEditing}
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">Store Name (English)</label>
                        <input
                          type="text"
                          name="storeNameEn"
                          className="form-input"
                          value={formData.storeNameEn}
                          onChange={handleChange}
                          disabled={!isEditing}
                          dir="ltr"
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">ŸàÿµŸÅ ÿßŸÑŸÖÿ™ÿ¨ÿ±</label>
                        <textarea
                          name="storeDescription"
                          className="form-textarea"
                          value={formData.storeDescription}
                          onChange={handleChange}
                          disabled={!isEditing}
                          rows={3}
                        />
                      </div>
                    </div>
                  </>
                )}

                {/* Action Buttons */}
                {isEditing && (
                  <div style={{ display: 'flex', gap: 'var(--space-md)', marginTop: 'var(--space-xl)' }}>
                    <button 
                      type="submit" 
                      className="btn btn-primary"
                      disabled={loading}
                    >
                      {loading ? (
                        <>
                          <span className="loading-spinner" style={{ width: '20px', height: '20px' }}></span>
                          <span>ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏...</span>
                        </>
                      ) : (
                        <>
                          <span>üíæ</span>
                          <span>ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿ∫ŸäŸäÿ±ÿßÿ™</span>
                        </>
                      )}
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-ghost"
                      onClick={handleCancel}
                      disabled={loading}
                    >
                      ÿ•ŸÑÿ∫ÿßÿ°
                    </button>
                  </div>
                )}
              </form>
            </div>
          </div>

          {/* Account Info Card */}
          <div className="card" style={{ marginTop: 'var(--space-xl)' }}>
            <div className="card-header">
              <h3>ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ≠ÿ≥ÿßÿ®</h3>
            </div>
            <div className="card-body">
              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                gap: 'var(--space-lg)'
              }}>
                <div>
                  <p style={{ color: 'var(--gray-500)', fontSize: '0.875rem' }}>ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ</p>
                  <p style={{ fontWeight: '500' }}>
                    {new Date(user?.createdAt).toLocaleDateString('ar-SA')}
                  </p>
                </div>
                <div>
                  <p style={{ color: 'var(--gray-500)', fontSize: '0.875rem' }}>ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿ≥ÿßÿ®</p>
                  <p style={{ 
                    fontWeight: '500',
                    color: user?.verified ? 'var(--success)' : 'var(--warning)'
                  }}>
                    {user?.verified ? '‚úì ŸÖŸàÿ´ŸÇ' : '‚è≥ ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©'}
                  </p>
                </div>
                <div>
                  <p style={{ color: 'var(--gray-500)', fontSize: '0.875rem' }}>ŸÖÿπÿ±ŸÅ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ</p>
                  <p style={{ fontWeight: '500', fontFamily: 'monospace', fontSize: '0.875rem' }}>
                    {user?.id}
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* Danger Zone */}
          <div className="card" style={{ marginTop: 'var(--space-xl)', borderColor: 'var(--error)' }}>
            <div className="card-header" style={{ background: '#fee2e2' }}>
              <h3 style={{ color: 'var(--error)' }}>‚ö†Ô∏è ŸÖŸÜÿ∑ŸÇÿ© ÿßŸÑÿÆÿ∑ÿ±</h3>
            </div>
            <div className="card-body">
              <p style={{ marginBottom: 'var(--space-lg)', color: 'var(--gray-600)' }}>
                Ÿáÿ∞Ÿá ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑÿ™ÿ±ÿßÿ¨ÿπ ÿπŸÜŸáÿß. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÇÿ®ŸÑ ÿßŸÑŸÖÿ™ÿßÿ®ÿπÿ©.
              </p>
              <div style={{ display: 'flex', gap: 'var(--space-md)', flexWrap: 'wrap' }}>
                <button className="btn btn-secondary" style={{ color: 'var(--error)', borderColor: 'var(--error)' }}>
                  ÿ™ÿ∫ŸäŸäÿ± ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±
                </button>
                <button className="btn btn-danger">
                  ÿ≠ÿ∞ŸÅ ÿßŸÑÿ≠ÿ≥ÿßÿ®
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Profile;
