import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Register = () => {
  const navigate = useNavigate();
  const { register, isAuthenticated, loading, error, clearError } = useAuth();
  
  // Form state
  const [step, setStep] = useState(1); // 1: Role selection, 2: User details
  const [formData, setFormData] = useState({
    role: '',
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    firstNameEn: '',
    lastNameEn: '',
    phone: '',
    city: '',
    // Seller-specific fields
    storeName: '',
    storeNameEn: '',
    storeDescription: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Saudi cities list
  const saudiCities = [
    'Ø§Ù„Ø±ÙŠØ§Ø¶ - Riyadh',
    'Ø¬Ø¯Ø© - Jeddah',
    'Ù…ÙƒØ© Ø§Ù„Ù…ÙƒØ±Ù…Ø© - Makkah',
    'Ø§Ù„Ù…Ø¯ÙŠÙ†Ø© Ø§Ù„Ù…Ù†ÙˆØ±Ø© - Madinah',
    'Ø§Ù„Ø¯Ù…Ø§Ù… - Dammam',
    'Ø§Ù„Ø®Ø¨Ø± - Khobar',
    'Ø§Ù„Ø¸Ù‡Ø±Ø§Ù† - Dhahran',
    'Ø§Ù„Ø·Ø§Ø¦Ù - Taif',
    'ØªØ¨ÙˆÙƒ - Tabuk',
    'Ø£Ø¨Ù‡Ø§ - Abha',
    'Ø§Ù„Ù‚ØµÙŠÙ… - Qassim',
    'Ø­Ø§Ø¦Ù„ - Hail',
    'Ù†Ø¬Ø±Ø§Ù† - Najran',
    'Ø¬Ø§Ø²Ø§Ù† - Jazan'
  ];

  // Redirect if already logged in
  useEffect(() => {
    if (isAuthenticated()) {
      navigate('/', { replace: true });
    }
  }, [isAuthenticated, navigate]);

  // Clear auth errors on mount
  useEffect(() => {
    clearError();
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear field error on change
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Handle role selection
  const handleRoleSelect = (role) => {
    setFormData(prev => ({
      ...prev,
      role
    }));
    setStep(2);
  };

  // Validate step 2 form
  const validateForm = () => {
    const errors = {};
    
    // Email validation
    if (!formData.email.trim()) {
      errors.email = 'Ø§Ù„Ø¨Ø±ÙŠØ¯ Ø§Ù„Ø¥Ù„ÙƒØªØ±ÙˆÙ†ÙŠ Ù…Ø·Ù„ÙˆØ¨';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      errors.email = 'ØµÙŠØºØ© Ø§Ù„Ø¨Ø±ÙŠØ¯ Ø§Ù„Ø¥Ù„ÙƒØªØ±ÙˆÙ†ÙŠ ØºÙŠØ± ØµØ­ÙŠØ­Ø©';
    }
    
    // Password validation
    if (!formData.password) {
      errors.password = 'ÙƒÙ„Ù…Ø© Ø§Ù„Ù…Ø±ÙˆØ± Ù…Ø·Ù„ÙˆØ¨Ø©';
    } else if (formData.password.length < 8) {
      errors.password = 'ÙƒÙ„Ù…Ø© Ø§Ù„Ù…Ø±ÙˆØ± ÙŠØ¬Ø¨ Ø£Ù† ØªÙƒÙˆÙ† 8 Ø£Ø­Ø±Ù Ø¹Ù„Ù‰ Ø§Ù„Ø£Ù‚Ù„';
    } else if (!/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(formData.password)) {
      errors.password = 'ÙŠØ¬Ø¨ Ø£Ù† ØªØ­ØªÙˆÙŠ Ø¹Ù„Ù‰ Ø­Ø±Ù ÙƒØ¨ÙŠØ± ÙˆØµØºÙŠØ± ÙˆØ±Ù‚Ù…';
    }
    
    // Confirm password
    if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = 'ÙƒÙ„Ù…Ø§Øª Ø§Ù„Ù…Ø±ÙˆØ± ØºÙŠØ± Ù…ØªØ·Ø§Ø¨Ù‚Ø©';
    }
    
    // Name validation
    if (!formData.firstName.trim()) {
      errors.firstName = 'Ø§Ù„Ø§Ø³Ù… Ø§Ù„Ø£ÙˆÙ„ Ù…Ø·Ù„ÙˆØ¨';
    }
    if (!formData.lastName.trim()) {
      errors.lastName = 'Ø§Ø³Ù… Ø§Ù„Ø¹Ø§Ø¦Ù„Ø© Ù…Ø·Ù„ÙˆØ¨';
    }
    
    // Phone validation
    if (!formData.phone.trim()) {
      errors.phone = 'Ø±Ù‚Ù… Ø§Ù„Ø¬ÙˆØ§Ù„ Ù…Ø·Ù„ÙˆØ¨';
    } else if (!/^(\+966|05)\d{8}$/.test(formData.phone.replace(/\s/g, ''))) {
      errors.phone = 'ØµÙŠØºØ© Ø±Ù‚Ù… Ø§Ù„Ø¬ÙˆØ§Ù„ ØºÙŠØ± ØµØ­ÙŠØ­Ø©';
    }
    
    // City validation
    if (!formData.city) {
      errors.city = 'Ø§Ù„Ù…Ø¯ÙŠÙ†Ø© Ù…Ø·Ù„ÙˆØ¨Ø©';
    }
    
    // Seller-specific validation
    if (formData.role === 'seller') {
      if (!formData.storeName.trim()) {
        errors.storeName = 'Ø§Ø³Ù… Ø§Ù„Ù…ØªØ¬Ø± Ù…Ø·Ù„ÙˆØ¨';
      }
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setIsSubmitting(true);
    
    // Prepare user data
    const userData = {
      email: formData.email,
      password: formData.password,
      firstName: formData.firstName,
      lastName: formData.lastName,
      firstNameEn: formData.firstNameEn || formData.firstName,
      lastNameEn: formData.lastNameEn || formData.lastName,
      phone: formData.phone,
      city: formData.city.split(' - ')[1] || formData.city, // Extract English city name
      role: formData.role
    };
    
    // Add seller-specific fields
    if (formData.role === 'seller') {
      userData.storeName = formData.storeName;
      userData.storeNameEn = formData.storeNameEn || formData.storeName;
      userData.storeDescription = formData.storeDescription;
    }
    
    const result = await register(userData);
    
    if (result.success) {
      navigate('/', { 
        replace: true,
        state: { message: 'ØªÙ… Ø¥Ù†Ø´Ø§Ø¡ Ø­Ø³Ø§Ø¨Ùƒ Ø¨Ù†Ø¬Ø§Ø­! Ù…Ø±Ø­Ø¨Ø§Ù‹ Ø¨Ùƒ ÙÙŠ Ø£Ø³Ø±Ù†Ø§' }
      });
    }
    
    setIsSubmitting(false);
  };

  // Go back to step 1
  const handleBack = () => {
    setStep(1);
    setFormErrors({});
  };

  return (
    <div className="auth-page">
      <div className="auth-container animate-fade-in">
        <div className="auth-card">
          {/* Header */}
          <div className="auth-header">
            <div className="auth-logo">ğŸŒŸ</div>
            <h1 className="auth-title">
              {step === 1 ? 'Ø§Ù†Ø¶Ù… Ù…' : 'Ø£ÙƒÙ…Ù„ Ø¨ÙŠØ§Ù†Ø§ØªÙƒ'}
            </h1>
            <p className="auth-subtitle">
              {step === 1 
                ? 'Ø§Ø®ØªØ± Ù†ÙˆØ¹ Ø­Ø³Ø§Ø¨Ùƒ Ù„Ù„Ø¨Ø¯Ø¡'
                : formData.role === 'seller' 
                  ? 'Ø³Ø¬Ù„ ÙƒØ¨Ø§Ø¦Ø¹ Ù„Ø¹Ø±Ø¶ Ù…Ù†ØªØ¬Ø§ØªÙƒ'
                  : 'Ø³Ø¬Ù„ ÙƒÙ…Ø´ØªØ±ÙŠ Ù„Ù„ØªØ³ÙˆÙ‚'}
            </p>
          </div>

          {/* Body */}
          <div className="auth-body">
            {/* Error Alert */}
            {error && (
              <div className="alert alert-error">
                <span>âš ï¸</span>
                <span>{error}</span>
              </div>
            )}

            {/* Step 1: Role Selection */}
            {step === 1 && (
              <div className="role-selector">
                <div 
                  className={`role-option ${formData.role === 'buyer' ? 'selected' : ''}`}
                  onClick={() => handleRoleSelect('buyer')}
                >
                  <div className="role-option-icon">ğŸ›’</div>
                  <div className="role-option-title">Ù…Ø´ØªØ±ÙŠ</div>
                  <div className="role-option-desc">ØªØ³ÙˆÙ‚ Ø§Ù„Ù…Ù†ØªØ¬Ø§Øª Ø§Ù„Ø³Ø¹ÙˆØ¯ÙŠØ©</div>
                </div>
                <div 
                  className={`role-option ${formData.role === 'seller' ? 'selected' : ''}`}
                  onClick={() => handleRoleSelect('seller')}
                >
                  <div className="role-option-icon">ğŸª</div>
                  <div className="role-option-title">Ø¨Ø§Ø¦Ø¹</div>
                  <div className="role-option-desc">Ø§Ø¹Ø±Ø¶ Ù…Ù†ØªØ¬Ø§ØªÙƒ Ù„Ù„Ø¨ÙŠØ¹</div>
                </div>
              </div>
            )}

            {/* Step 2: Registration Form */}
            {step === 2 && (
              <form onSubmit={handleSubmit}>
                {/* Back Button */}
                <button
                  type="button"
                  className="btn btn-ghost mb-lg"
                  onClick={handleBack}
                >
                  <span>â†’</span>
                  <span>ØªØºÙŠÙŠØ± Ù†ÙˆØ¹ Ø§Ù„Ø­Ø³Ø§Ø¨</span>
                </button>

                {/* Email */}
                <div className="form-group">
                  <label className="form-label required" htmlFor="email">
                    Ø§Ù„Ø¨Ø±ÙŠØ¯ Ø§Ù„Ø¥Ù„ÙƒØªØ±ÙˆÙ†ÙŠ
                  </label>
                  <input
                    type="email"
