import React, { createContext, useContext, useState, useEffect } from 'react';
import { useAuth } from './AuthContext';
import {
    getUserCart,
    addToCart as dbAddToCart,
    updateCartQuantity as dbUpdateQuantity,
    removeFromCart as dbRemoveFromCart,
    clearCart as dbClearCart,
    getProductById
} from '../utils/database'; // üö® ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿßÿ≥ÿ™Ÿäÿ±ÿßÿØÿßÿ™ ŸÑÿ™ÿ¥ŸÖŸÑ ÿ¨ŸÖŸäÿπ ÿØŸàÿßŸÑ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™

// Create Cart Context
const CartContext = createContext(null);

// Cart Provider Component
export const CartProvider = ({ children }) => {
    const { user, isAuthenticated, loading: authLoading } = useAuth(); // üö® ÿ•ÿ∂ÿßŸÅÿ© isAuthenticated Ÿà authLoading
    const [cartItems, setCartItems] = useState([]);
    const [loading, setLoading] = useState(false); // üö® ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿ≠ÿßŸÑÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿÆÿßÿµÿ© ÿ®ÿßŸÑÿ≥ŸÑÿ©

    // Load user's cart from database
    const loadCart = () => {
        if (!user) {
            setCartItems([]);
            return;
        }

        setLoading(true);
        try {
            const cart = getUserCart(user.id);
            // Enrich cart items with product details
            const enrichedCart = cart.map(item => {
                const product = getProductById(item.productId);
                return {
                    ...item,
                    product
                };
            }).filter(item => item.product); // Remove items with deleted products
            
            setCartItems(enrichedCart);
        } catch (error) {
            console.error("Error loading cart:", error);
        } finally {
            setLoading(false);
        }
    };
    
    // Load cart when user changes (ŸÖŸÜÿ∑ŸÇ ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸàŸÑŸä)
    useEffect(() => {
        // ÿßŸÑÿßŸÜÿ™ÿ∏ÿßÿ± ÿ≠ÿ™Ÿâ ŸäŸÜÿ™ŸáŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿµÿßÿØŸÇÿ© ŸàŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
        if (authLoading) return; 

        if (isAuthenticated() && user) {
            loadCart();
        } else {
            setCartItems([]);
        }
    }, [user, authLoading]); // üö® ÿ™ŸÖ ÿ±ÿ®ÿ∑ ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸÄ user Ÿà authLoading

    // Add item to cart
    const addToCart = (productId, quantity = 1) => {
        if (!user) {
            return { success: false, error: 'Ÿäÿ¨ÿ® ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ£ŸàŸÑÿßŸã - Please login first' };
        }

        setLoading(true);
        try {
            dbAddToCart(user.id, productId, quantity);
            loadCart(); // ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ≥ŸÑÿ© ÿ®ÿπÿØ ÿßŸÑÿ™ÿπÿØŸäŸÑ
            return { success: true };
        } catch (error) {
            return { success: false, error: error.message };
        } finally {
            setLoading(false);
        }
    };

    // Update item quantity
    const updateQuantity = (productId, quantity) => {
        if (!user) return { success: false };

        setLoading(true);
        try {
            dbUpdateQuantity(user.id, productId, quantity);
            loadCart(); // ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ≥ŸÑÿ© ÿ®ÿπÿØ ÿßŸÑÿ™ÿπÿØŸäŸÑ
            return { success: true };
        } catch (error) {
            return { success: false, error: error.message };
        } finally {
            setLoading(false);
        }
    };

    // Remove item from cart
    const removeFromCart = (productId) => {
        if (!user) return { success: false };

        setLoading(true);
        try {
            dbRemoveFromCart(user.id, productId);
            loadCart(); // ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ≥ŸÑÿ© ÿ®ÿπÿØ ÿßŸÑÿ™ÿπÿØŸäŸÑ
            return { success: true };
        } catch (error) {
            return { success: false, error: error.message };
        } finally {
            setLoading(false);
        }
    };

    // Clear entire cart
    const clearCart = () => {
        if (!user) return { success: false };

        setLoading(true);
        try {
            dbClearCart(user.id);
            setCartItems([]); // ŸÖÿ≥ÿ≠ ŸÅŸàÿ±Ÿä ŸÑŸàÿßÿ¨Ÿáÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
            return { success: true };
        } catch (error) {
            return { success: false, error: error.message };
        } finally {
            setLoading(false);
        }
    };

    // Calculate cart totals
    const getCartTotal = () => {
        return cartItems.reduce((total, item) => {
            return total + (item.product?.price || 0) * item.quantity;
        }, 0);
    };

    // Get total items count
    const getItemCount = () => {
        return cartItems.reduce((count, item) => count + item.quantity, 0);
    };

    // Check if product is in cart
    const isInCart = (productId) => {
        return cartItems.some(item => item.productId === productId);
    };

    // Get item quantity in cart
    const getItemQuantity = (productId) => {
        const item = cartItems.find(item => item.productId === productId);
        return item ? item.quantity : 0;
    };

    const value = {
        cartItems,
        loading, // üö® ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿ≠ÿßŸÑÿ© ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ
        addToCart,
        updateQuantity, // üö® ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©
        removeFromCart, // üö® ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©
        clearCart, // üö® ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©
        getCartTotal, // üö® ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©
        getItemCount, // üö® ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©
        isInCart, // üö® ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©
        getItemQuantity, // üö® ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©
        loadCart
    };

    return (
        <CartContext.Provider value={value}>
            {children}
        </CartContext.Provider>
    );
};

// Custom hook to use cart context
export const useCart = () => {
    const context = useContext(CartContext);
    if (!context) {
        throw new Error('useCart must be used within a CartProvider');
    }
    return context;
};

export default CartContext;
