import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';
import { getFeaturedProducts, getAllProducts } from '../utils/database';
import Navbar from '../components/Navbar';

const Home = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated, isSeller } = useAuth();
  const { addToCart } = useCart();
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [categories] = useState([
    { id: 'food', name: 'Ù…Ø£ÙƒÙˆÙ„Ø§Øª', nameEn: 'Food', icon: 'ðŸ¯', color: '#fef3c7' },
    { id: 'fragrance', name: 'Ø¹Ø·ÙˆØ± ÙˆØ¨Ø®ÙˆØ±', nameEn: 'Fragrance', icon: 'ðŸŒ¸', color: '#fce7f3' },
    { id: 'crafts', name: 'Ø­Ø±Ù ÙŠØ¯ÙˆÙŠØ©', nameEn: 'Crafts', icon: 'ðŸŽ¨', color: '#dbeafe' },
    { id: 'clothing', name: 'Ù…Ù„Ø§Ø¨Ø³ ØªØ±Ø§Ø«ÙŠØ©', nameEn: 'Clothing', icon: 'ðŸ‘˜', color: '#d1fae5' },
    { id: 'dates', name: 'ØªÙ…ÙˆØ±', nameEn: 'Dates', icon: 'ðŸŒ´', color: '#fde68a' },
    { id: 'coffee', name: 'Ù‚Ù‡ÙˆØ© Ø¹Ø±Ø¨ÙŠØ©', nameEn: 'Coffee', icon: 'â˜•', color: '#e9d5ff' }
  ]);

  useEffect(() => {
    // Load featured products
    const featured = getFeaturedProducts();
    if (featured.length < 4) {
      // If not enough featured, get some regular products
      const all = getAllProducts();
      setFeaturedProducts([...featured, ...all.filter(p => !p.featured)].slice(0, 6));
    } else {
      setFeaturedProducts(featured.slice(0, 6));
    }
  }, []);

  const handleAddToCart = (productId) => {
    if (!isAuthenticated()) {
      navigate('/login', { state: { from: '/' } });
      return;
    }
    addToCart(productId, 1);
  };

  return (
    <div>
      <Navbar />
      
      {/* Hero Section */}
      <section 
        style={{
          background: 'linear-gradient(135deg, var(--primary) 0%, var(--primary-dark) 100%)',
          color: 'var(--white)',
          padding: 'var(--space-3xl) var(--space-lg)',
          textAlign: 'center',
          position: 'relative',
          overflow: 'hidden'
        }}
      >
        {/* Decorative Pattern */}
        <div style={{
          position: 'absolute',
