import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Register = () => {
  const navigate = useNavigate();
  const { register, isAuthenticated, loading, error, clearError } = useAuth();
  
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    firstNameEn: '',
    lastNameEn: '',
    phone: '',
    city: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const saudiCities = [
    'ÿßŸÑÿ±Ÿäÿßÿ∂ - Riyadh',
    'ÿ¨ÿØÿ© - Jeddah',
    'ŸÖŸÉÿ© ÿßŸÑŸÖŸÉÿ±ŸÖÿ© - Makkah',
    'ÿßŸÑŸÖÿØŸäŸÜÿ© ÿßŸÑŸÖŸÜŸàÿ±ÿ© - Madinah',
    'ÿßŸÑÿØŸÖÿßŸÖ - Dammam',
    'ÿßŸÑÿÆÿ®ÿ± - Khobar',
    'ÿßŸÑÿ∏Ÿáÿ±ÿßŸÜ - Dhahran',
    'ÿßŸÑÿ∑ÿßÿ¶ŸÅ - Taif',
    'ÿ™ÿ®ŸàŸÉ - Tabuk',
    'ÿ£ÿ®Ÿáÿß - Abha',
    'ÿßŸÑŸÇÿµŸäŸÖ - Qassim',
    'ÿ≠ÿßÿ¶ŸÑ - Hail',
    'ŸÜÿ¨ÿ±ÿßŸÜ - Najran',
    'ÿ¨ÿßÿ≤ÿßŸÜ - Jazan'
  ];

  useEffect(() => {
    if (isAuthenticated()) {
      navigate('/', { replace: true });
    }
  }, [isAuthenticated, navigate]);

  useEffect(() => {
    clearError();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const errors = {};
    
    if (!formData.email.trim()) {
      errors.email = 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸÖÿ∑ŸÑŸàÿ®';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      errors.email = 'ÿµŸäÿ∫ÿ© ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©';
    }
    
    if (!formData.password) {
      errors.password = 'ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ŸÖÿ∑ŸÑŸàÿ®ÿ©';
    } else if (formData.password.length < 8) {
      errors.password = 'ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ 8 ÿ£ÿ≠ÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ';
    } else if (!/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(formData.password)) {
      errors.password = 'Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿ≠ÿ±ŸÅ ŸÉÿ®Ÿäÿ± Ÿàÿµÿ∫Ÿäÿ± Ÿàÿ±ŸÇŸÖ';
    }
    
    if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = 'ŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ŸÖÿ™ÿ∑ÿßÿ®ŸÇÿ©';
    }
    
    if (!formData.firstName.trim()) {
      errors.firstName = 'ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ ŸÖÿ∑ŸÑŸàÿ®';
    }
    if (!formData.lastName.trim()) {
      errors.lastName = 'ÿßÿ≥ŸÖ ÿßŸÑÿπÿßÿ¶ŸÑÿ© ŸÖÿ∑ŸÑŸàÿ®';
    }
    
    if (!formData.phone.trim()) {
      errors.phone = 'ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ ŸÖÿ∑ŸÑŸàÿ®';
    } else if (!/^(\+966|05)\d{8}$/.test(formData.phone.replace(/\s/g, ''))) {
      errors.phone = 'ÿµŸäÿ∫ÿ© ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©';
    }
    
    if (!formData.city) {
      errors.city = 'ÿßŸÑŸÖÿØŸäŸÜÿ© ŸÖÿ∑ŸÑŸàÿ®ÿ©';
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setIsSubmitting(true);
    
    const userData = {
      email: formData.email,
      password: formData.password,
      firstName: formData.firstName,
      lastName: formData.lastName,
      firstNameEn: formData.firstNameEn || formData.firstName,
      lastNameEn: formData.lastNameEn || formData.lastName,
      phone: formData.phone,
      city: formData.city.split(' - ')[1] || formData.city,
      role: 'buyer'
    };
    
    const result = await register(userData);
    
    if (result.success) {
      navigate('/', { 
        replace: true,
        state: { message: 'ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿ≠ÿ≥ÿßÿ®ŸÉ ÿ®ŸÜÿ¨ÿßÿ≠! ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ ŸÅŸä TechStore' }
      });
    }
    
    setIsSubmitting(false);
  };

  return (
    <div className="auth-page" style={{
      background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)'
    }}>
      <div className="auth-container animate-fade-in">
        <div className="auth-card">
          {/* Header */}
          <div className="auth-header" style={{
            background: 'linear-gradient(135deg, #1a1a2e 0%, #0f3460 100%)'
          }}>
            <div className="auth-logo" style={{
              background: 'rgba(0, 255, 136, 0.2)',
              border: '2px solid rgba(0, 255, 136, 0.3)'
            }}>
              üñ•Ô∏è
            </div>
            <h1 className="auth-title">ÿ•ŸÜÿ¥ÿßÿ° ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ</h1>
            <p className="auth-subtitle">ÿßŸÜÿ∂ŸÖ ÿ•ŸÑŸâ TechStore ÿßŸÑÿ¢ŸÜ</p>
          </div>

          {/* Body */}
          <div className="auth-body">
            {/* Error Alert */}
            {error && (
              <div className="alert alert-error">
                <span>‚ö†Ô∏è</span>
                <span>{error}</span>
              </div>
            )}

            {/* Registration Form */}
            <form onSubmit={handleSubmit}>
              {/* Email */}
              <div className="form-group">
                <label className="form-label required" htmlFor="email">
                  ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä
                </label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  className={`form-input ${formErrors.email ? 'error' : ''}`}
                  placeholder="example@email.com"
                  value={formData.email}
                  onChange={handleChange}
                  disabled={isSubmitting}
                  dir="ltr"
                />
                {formErrors.email && (
                  <span className="form-error">{formErrors.email}</span>
                )}
              </div>

              {/* Password */}
              <div className="form-group">
                <label className="form-label required" htmlFor="password">
                  ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±
                </label>
                <div style={{ position: 'relative' }}>
                  <input
                    type={showPassword ? 'text' : 'password'}
                    id="password"
                    name="password"
                    className={`form-input ${formErrors.password ? 'error' : ''}`}
                    placeholder="8 ÿ£ÿ≠ÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ"
                    value={formData.password}
                    onChange={handleChange}
                    disabled={isSubmitting}
                    dir="ltr"
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    style={{
                      position: 'absolute',
                      left: '12px',
                      top: '50%',
                      transform: 'translateY(-50%)',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      color: 'var(--gray-400)'
                    }}
                  >
                    {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                  </button>
                </div>
                {formErrors.password && (
                  <span className="form-error">{formErrors.password}</span>
                )}
                <span className="form-hint">
                  Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿ≠ÿ±ŸÅ ŸÉÿ®Ÿäÿ± Ÿàÿµÿ∫Ÿäÿ± Ÿàÿ±ŸÇŸÖ
                </span>
              </div>

              {/* Confirm Password */}
              <div className="form-group">
                <label className="form-label required" htmlFor="confirmPassword">
                  ÿ™ÿ£ŸÉŸäÿØ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±
                </label>
                <input
                  type={showPassword ? 'text' : 'password'}
                  id="confirmPassword"
                  name="confirmPassword"
                  className={`form-input ${formErrors.confirmPassword ? 'error' : ''}`}
                  placeholder="ÿ£ÿπÿØ ÿ•ÿØÿÆÿßŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  disabled={isSubmitting}
                  dir="ltr"
                />
                {formErrors.confirmPassword && (
                  <span className="form-error">{formErrors.confirmPassword}</span>
                )}
              </div>

              {/* Name Fields */}
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 'var(--space-md)' }}>
                <div className="form-group">
                  <label className="form-label required" htmlFor="firstName">
                    ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ
                  </label>
                  <input
                    type="text"
                    id="firstName"
                    name="firstName"
                    className={`form-input ${formErrors.firstName ? 'error' : ''}`}
                    placeholder="ŸÖÿ≠ŸÖÿØ"
                    value={formData.firstName}
                    onChange={handleChange}
                    disabled={isSubmitting}
                  />
                  {formErrors.firstName && (
                    <span className="form-error">{formErrors.firstName}</span>
                  )}
                </div>
                <div className="form-group">
                  <label className="form-label required" htmlFor="lastName">
                    ÿßÿ≥ŸÖ ÿßŸÑÿπÿßÿ¶ŸÑÿ©
                  </label>
                  <input
                    type="text"
                    id="lastName"
                    name="lastName"
                    className={`form-input ${formErrors.lastName ? 'error' : ''}`}
                    placeholder="ÿßŸÑÿ≥ÿπŸàÿØŸä"
                    value={formData.lastName}
                    onChange={handleChange}
                    disabled={isSubmitting}
                  />
                  {formErrors.lastName && (
                    <span className="form-error">{formErrors.lastName}</span>
                  )}
                </div>
              </div>

              {/* Phone */}
              <div className="form-group">
                <label className="form-label required" htmlFor="phone">
                  ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ
                </label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  className={`form-input ${formErrors.phone ? 'error' : ''}`}
                  placeholder="+966 5X XXX XXXX"
                  value={formData.phone}
                  onChange={handleChange}
                  disabled={isSubmitting}
                  dir="ltr"
                />
                {formErrors.phone && (
                  <span className="form-error">{formErrors.phone}</span>
                )}
              </div>

              {/* City */}
              <div className="form-group">
                <label className="form-label required" htmlFor="city">
                  ÿßŸÑŸÖÿØŸäŸÜÿ©
                </label>
                <select
                  id="city"
                  name="city"
                  className={`form-select ${formErrors.city ? 'error' : ''}`}
                  value={formData.city}
                  onChange={handleChange}
                  disabled={isSubmitting}
                >
                  <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÖÿØŸäŸÜÿ©</option>
                  {saudiCities.map(city => (
                    <option key={city} value={city}>{city}</option>
                  ))}
                </select>
                {formErrors.city && (
                  <span className="form-error">{formErrors.city}</span>
                )}
              </div>

              {/* Terms */}
              <div className="form-group" style={{ fontSize: '0.875rem', color: 'var(--gray-600)' }}>
                <p>
                  ÿ®ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑÿå ÿ£ŸÜÿ™ ÿ™ŸàÿßŸÅŸÇ ÿπŸÑŸâ{' '}
                  <Link to="/terms" style={{ textDecoration: 'underline' }}>
                    ÿ¥ÿ±Ÿàÿ∑ ÿßŸÑÿßÿ≥ÿ™ÿÆÿØÿßŸÖ
                  </Link>
                  {' '}Ÿà{' '}
                  <Link to="/privacy" style={{ textDecoration: 'underline' }}>
                    ÿ≥Ÿäÿßÿ≥ÿ© ÿßŸÑÿÆÿµŸàÿµŸäÿ©
                  </Link>
                </p>
              </div>

              {/* Submit Button */}
              <button
                type="submit"
                className="btn btn-primary btn-full btn-lg"
                disabled={isSubmitting || loading}
                style={{
                  background: 'linear-gradient(135deg, #00ff88 0%, #00cc6a 100%)',
                  color: '#1a1a2e'
                }}
              >
                {isSubmitting ? (
                  <>
                    <span className="loading-spinner" style={{ width: '20px', height: '20px' }}></span>
                    <span>ÿ¨ÿßÿ±Ÿä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ≠ÿ≥ÿßÿ®...</span>
                  </>
                ) : (
                  <>
                    <span>ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ≠ÿ≥ÿßÿ®</span>
                    <span>‚Üê</span>
                  </>
                )}
              </button>
            </form>
          </div>

          {/* Footer */}
          <div className="auth-footer">
            <p>
              ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ® ÿ®ÿßŸÑŸÅÿπŸÑÿü{' '}
              <Link to="/login" style={{ fontWeight: '600', color: '#00cc6a' }}>
                ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
              </Link>
            </p>
          </div>
        </div>

        {/* Back to Home */}
        <div style={{ textAlign: 'center', marginTop: 'var(--space-lg)' }}>
          <Link 
            to="/" 
            style={{ 
              color: 'rgba(255,255,255,0.7)',
              display: 'inline-flex',
              alignItems: 'center',
              gap: 'var(--space-sm)'
            }}
          >
            <span>‚Üí</span>
            <span>ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©</span>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Register;
