import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';
import { getAllProducts } from '../utils/database';
import Navbar from '../components/Navbar';

const Home = () => {
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();
  const { addToCart } = useCart();
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [addedToCart, setAddedToCart] = useState(null);

  useEffect(() => {
    const products = getAllProducts();
    const featured = products.filter(p => p.featured).slice(0, 8);
    setFeaturedProducts(featured);
  }, []);

  const handleAddToCart = (productId) => {
    if (!isAuthenticated()) {
      navigate('/login');
      return;
    }
    addToCart(productId, 1);
    setAddedToCart(productId);
    setTimeout(() => setAddedToCart(null), 2000);
  };

  const categories = [
    { id: 'gpu', name: 'ŸÉÿ±Ÿàÿ™ ÿßŸÑÿ¥ÿßÿ¥ÿ©', icon: 'üéÆ', color: '#fee2e2' },
    { id: 'cpu', name: 'ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿßÿ™', icon: '‚ö°', color: '#dbeafe' },
    { id: 'ram', name: 'ÿßŸÑÿ∞ÿßŸÉÿ±ÿ© RAM', icon: 'üß†', color: '#d1fae5' },
    { id: 'storage', name: 'ÿßŸÑÿ™ÿÆÿ≤ŸäŸÜ SSD', icon: 'üíæ', color: '#fef3c7' },
    { id: 'motherboard', name: 'ÿßŸÑŸÑŸàÿ≠ÿßÿ™ ÿßŸÑÿ£ŸÖ', icon: 'üîß', color: '#e9d5ff' },
    { id: 'psu', name: 'ÿßŸÑÿ∑ÿßŸÇÿ© PSU', icon: 'üîå', color: '#fce7f3' },
    { id: 'cooling', name: 'ÿßŸÑÿ™ÿ®ÿ±ŸäÿØ', icon: '‚ùÑÔ∏è', color: '#cffafe' },
    { id: 'case', name: 'ÿßŸÑŸÉŸäÿ≥ÿßÿ™', icon: 'üñ•Ô∏è', color: '#f3f4f6' }
  ];

  const getCategoryIcon = (category) => {
    const cat = categories.find(c => c.id === category);
    return cat ? cat.icon : 'üì¶';
  };

  return (
    <div>
      <Navbar />

      {/* Hero Section */}
      <section style={{
        background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)',
        padding: 'var(--space-3xl) var(--space-lg)',
        color: 'var(--white)',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div className="container" style={{ position: 'relative', zIndex: 1 }}>
          <div style={{ maxWidth: '700px', margin: '0 auto', textAlign: 'center' }}>
            <span style={{
              display: 'inline-block',
              background: 'rgba(0, 255, 136, 0.2)',
              color: '#00ff88',
              padding: 'var(--space-xs) var(--space-md)',
              borderRadius: 'var(--radius-full)',
              fontSize: '0.875rem',
              fontWeight: '600',
              marginBottom: 'var(--space-lg)',
              border: '1px solid rgba(0, 255, 136, 0.3)'
            }}>
              üéÆ ŸÖÿ™ÿ¨ÿ±ŸÉ ÿßŸÑÿ£ŸàŸÑ ŸÑŸÇÿ∑ÿπ ÿßŸÑŸÉŸÖÿ®ŸäŸàÿ™ÿ±
            </span>
            
            <h1 style={{
              fontSize: '3rem',
              fontWeight: '800',
              marginBottom: 'var(--space-lg)',
              color: 'var(--white)',
              textShadow: '0 0 40px rgba(0, 255, 136, 0.3)'
            }}>
              üñ•Ô∏è TechStore
            </h1>
            
            <p style={{
              fontSize: '1.25rem',
              opacity: 0.9,
              marginBottom: 'var(--space-xl)',
              lineHeight: '1.8'
            }}>
              ÿ£ÿ≠ÿØÿ´ ŸÇÿ∑ÿπ ÿßŸÑŸÉŸÖÿ®ŸäŸàÿ™ÿ± Ÿàÿ£ŸÉÿ≥ÿ≥Ÿàÿßÿ±ÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ®ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿ£ÿ≥ÿπÿßÿ± ŸÅŸä ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©
            </p>

            <div style={{
              display: 'flex',
              gap: 'var(--space-md)',
              justifyContent: 'center',
              flexWrap: 'wrap',
              marginBottom: 'var(--space-2xl)'
            }}>
              <Link
                to="/products"
                className="btn btn-lg"
                style={{
                  background: 'linear-gradient(135deg, #00ff88 0%, #00cc6a 100%)',
                  color: '#1a1a2e',
                  fontWeight: '700',
                  padding: 'var(--space-md) var(--space-2xl)'
                }}
              >
                ÿ™ÿ≥ŸàŸÇ ÿßŸÑÿ¢ŸÜ üõí
              </Link>
              <Link
                to="/products?category=gpu"
                className="btn btn-lg"
                style={{
                  background: 'rgba(255,255,255,0.1)',
                  color: 'var(--white)',
                  border: '2px solid rgba(255,255,255,0.3)'
                }}
              >
                üéÆ ŸÉÿ±Ÿàÿ™ ÿßŸÑÿ¥ÿßÿ¥ÿ©
              </Link>
            </div>

            {/* Stats */}
            <div style={{
              display: 'flex',
              justifyContent: 'center',
              gap: 'var(--space-2xl)',
              flexWrap: 'wrap'
            }}>
              <div style={{ textAlign: 'center' }}>
                <div style={{ fontSize: '2rem', fontWeight: '800', color: '#00ff88' }}>500+</div>
                <div style={{ opacity: 0.7, fontSize: '0.875rem' }}>ŸÖŸÜÿ™ÿ¨ ŸÖÿ™ŸàŸÅÿ±</div>
              </div>
              <div style={{ textAlign: 'center' }}>
                <div style={{ fontSize: '2rem', fontWeight: '800', color: '#00ff88' }}>10K+</div>
                <div style={{ opacity: 0.7, fontSize: '0.875rem' }}>ÿπŸÖŸäŸÑ ÿ≥ÿπŸäÿØ</div>
              </div>
              <div style={{ textAlign: 'center' }}>
                <div style={{ fontSize: '2rem', fontWeight: '800', color: '#00ff88' }}>24/7</div>
                <div style={{ opacity: 0.7, fontSize: '0.875rem' }}>ÿØÿπŸÖ ŸÅŸÜŸä</div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Brands Section */}
      <section style={{
        padding: 'var(--space-xl) var(--space-lg)',
        background: 'var(--white)',
        borderBottom: '1px solid var(--gray-100)'
      }}>
        <div className="container">
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            gap: 'var(--space-2xl)',
            flexWrap: 'wrap',
            opacity: 0.6
          }}>
            <span style={{ fontSize: '1.5rem', fontWeight: '700' }}>NVIDIA</span>
            <span style={{ fontSize: '1.5rem', fontWeight: '700' }}>AMD</span>
            <span style={{ fontSize: '1.5rem', fontWeight: '700' }}>Intel</span>
            <span style={{ fontSize: '1.5rem', fontWeight: '700' }}>ASUS</span>
            <span style={{ fontSize: '1.5rem', fontWeight: '700' }}>MSI</span>
            <span style={{ fontSize: '1.5rem', fontWeight: '700' }}>Corsair</span>
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section style={{ padding: 'var(--space-3xl) var(--space-lg)', background: 'var(--gray-50)' }}>
        <div className="container">
          <div style={{ textAlign: 'center', marginBottom: 'var(--space-2xl)' }}>
            <h2 style={{ marginBottom: 'var(--space-sm)' }}>üìÇ ÿ™ÿµŸÅÿ≠ ÿ≠ÿ≥ÿ® ÿßŸÑŸÅÿ¶ÿ©</h2>
            <p style={{ color: 'var(--gray-500)' }}>ÿßÿÆÿ™ÿ± ÿßŸÑŸÇÿ≥ŸÖ ÿßŸÑÿ∞Ÿä ÿ™ÿ±ŸäÿØŸá</p>
          </div>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
            gap: 'var(--space-lg)'
          }}>
            {categories.map(category => (
              <Link
                key={category.id}
                to={`/products?category=${category.id}`}
                style={{
                  textDecoration: 'none',
                  textAlign: 'center',
                  padding: 'var(--space-xl)',
                  background: category.color,
                  borderRadius: 'var(--radius-lg)',
                  transition: 'all var(--transition-normal)'
                }}
              >
                <div style={{ fontSize: '2.5rem', marginBottom: 'var(--space-sm)' }}>
                  {category.icon}
                </div>
                <div style={{ fontWeight: '600', color: 'var(--gray-800)' }}>
                  {category.name}
                </div>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Products */}
      <section style={{ padding: 'var(--space-3xl) var(--space-lg)' }}>
        <div className="container">
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            marginBottom: 'var(--space-2xl)',
            flexWrap: 'wrap',
            gap: 'var(--space-md)'
          }}>
            <div>
              <h2 style={{ marginBottom: 'var(--space-xs)' }}>üî• ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿßŸÑŸÖŸÖŸäÿ≤ÿ©</h2>
              <p style={{ color: 'var(--gray-500)' }}>ÿ£ŸÅÿ∂ŸÑ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿßŸÑŸÖÿÆÿ™ÿßÿ±ÿ© ŸÑŸÉ</p>
            </div>
            <Link to="/products" className="btn btn-secondary">
              ÿπÿ±ÿ∂ ÿßŸÑŸÉŸÑ ‚Üê
            </Link>
          </div>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
            gap: 'var(--space-lg)'
          }}>
            {featuredProducts.map(product => (
              <div 
                key={product.id} 
                className="card"
                style={{ overflow: 'hidden' }}
              >
                {/* Product Image */}
                <div style={{
                  height: '200px',
                  background: '#f8f9fa',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  padding: '20px',
                  position: 'relative'
                }}>
                  {product.image ? (
                    <img 
                      src={product.image} 
                      alt={product.name}
                      style={{
                        maxWidth: '100%',
                        maxHeight: '100%',
                        objectFit: 'contain'
                      }}
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                  ) : null}
                  <div style={{
                    display: product.image ? 'none' : 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    width: '100%',
                    height: '100%',
                    fontSize: '4rem',
                    background: 'linear-gradient(135deg, #1a1a2e 0%, #2d2d44 100%)',
                    color: '#00ff88'
                  }}>
                    {getCategoryIcon(product.category)}
                  </div>
                  
                  <span style={{
                    position: 'absolute',
                    top: '10px',
                    right: '10px',
                    background: '#ff6b6b',
                    color: 'white',
                    padding: '4px 10px',
                    borderRadius: 'var(--radius-full)',
                    fontSize: '0.75rem',
                    fontWeight: '600'
                  }}>
                    ŸÖŸÖŸäÿ≤ üî•
                  </span>
                </div>

                {/* Product Content */}
                <div style={{ padding: 'var(--space-lg)' }}>
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center', 
                    marginBottom: 'var(--space-sm)' 
                  }}>
                    <span style={{
                      background: 'var(--primary-lighter)',
                      color: 'var(--primary)',
                      padding: '2px 8px',
                      borderRadius: 'var(--radius-sm)',
                      fontSize: '0.75rem',
                      fontWeight: '500'
                    }}>
                      {categories.find(c => c.id === product.category)?.name || product.category}
                    </span>
                    <span style={{ fontSize: '0.75rem', color: 'var(--gray-500)', fontWeight: '600' }}>
                      {product.brand}
                    </span>
                  </div>
                  
                  <h3 style={{ 
                    fontSize: '1rem', 
                    marginBottom: 'var(--space-sm)',
                    color: 'var(--gray-800)'
                  }}>
                    {product.name}
                  </h3>
                  
                  <p style={{
                    fontSize: '0.875rem',
                    color: 'var(--gray-500)',
                    marginBottom: 'var(--space-sm)',
                    lineHeight: '1.5'
                  }}>
                    {product.description}
                  </p>
                  
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: 'var(--space-xs)',
                    marginBottom: 'var(--space-sm)'
                  }}>
                    <span style={{ color: '#f59e0b' }}>‚≠ê</span>
                    <span style={{ fontWeight: '600' }}>{product.rating}</span>
                    <span style={{ color: 'var(--gray-400)', fontSize: '0.875rem' }}>
                      ({product.reviewCount})
                    </span>
                  </div>
                  
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center',
                    marginBottom: 'var(--space-md)'
                  }}>
                    <p style={{ 
                      fontWeight: '700', 
                      fontSize: '1.25rem',
                      color: 'var(--primary)',
                      margin: 0
                    }}>
                      {product.price.toLocaleString()} <span style={{ fontSize: '0.875rem' }}>ÿ±.ÿ≥</span>
                    </p>
                  </div>
                  
                  <div style={{ display: 'flex', gap: 'var(--space-sm)' }}>
                    <Link 
                      to={`/products/${product.id}`} 
                      className="btn btn-secondary"
                      style={{ flex: 1, textAlign: 'center' }}
                    >
                      ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ
                    </Link>
                    <button 
                      className="btn btn-primary"
                      style={{ 
                        flex: 1,
                        background: addedToCart === product.id ? 'var(--success)' : ''
                      }}
                      onClick={() => handleAddToCart(product.id)}
                    >
                      {addedToCart === product.id ? '‚úì ÿ™ŸÖÿ™ ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©' : 'üõí ÿ£ÿ∂ŸÅ'}
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Why Us Section */}
      <section style={{ padding: 'var(--space-3xl) var(--space-lg)', background: 'var(--gray-50)' }}>
        <div className="container">
          <div style={{ textAlign: 'center', marginBottom: 'var(--space-2xl)' }}>
            <h2 style={{ marginBottom: 'var(--space-sm)' }}>‚ú® ŸÑŸÖÿßÿ∞ÿß TechStoreÿü</h2>
            <p style={{ color: 'var(--gray-500)' }}>ŸÜŸÇÿØŸÖ ŸÑŸÉ ÿ£ŸÅÿ∂ŸÑ ÿ™ÿ¨ÿ±ÿ®ÿ© ÿ™ÿ≥ŸàŸÇ</p>
          </div>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: 'var(--space-xl)'
          }}>
            <div className="card" style={{ textAlign: 'center', padding: 'var(--space-xl)' }}>
              <div style={{ fontSize: '3rem', marginBottom: 'var(--space-md)' }}>‚úÖ</div>
              <h4 style={{ marginBottom: 'var(--space-sm)' }}>ŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿ£ÿµŸÑŸäÿ©</h4>
              <p style={{ color: 'var(--gray-500)', fontSize: '0.9rem' }}>
                ÿ¨ŸÖŸäÿπ ŸÖŸÜÿ™ÿ¨ÿßÿ™ŸÜÿß ÿ£ÿµŸÑŸäÿ© 100% ŸÖÿπ ÿ∂ŸÖÿßŸÜ ÿßŸÑŸàŸÉŸäŸÑ
              </p>
            </div>
            <div className="card" style={{ textAlign: 'center', padding: 'var(--space-xl)' }}>
              <div style={{ fontSize: '3rem', marginBottom: 'var(--space-md)' }}>üöö</div>
              <h4 style={{ marginBottom: 'var(--space-sm)' }}>ÿ¥ÿ≠ŸÜ ÿ≥ÿ±Ÿäÿπ</h4>
              <p style={{ color: 'var(--gray-500)', fontSize: '0.9rem' }}>
                ÿ™ŸàÿµŸäŸÑ ÿÆŸÑÿßŸÑ 24-48 ÿ≥ÿßÿπÿ© ŸÑÿ¨ŸÖŸäÿπ ŸÖÿØŸÜ ÿßŸÑŸÖŸÖŸÑŸÉÿ©
              </p>
            </div>
            <div className="card" style={{ textAlign: 'center', padding: 'var(--space-xl)' }}>
              <div style={{ fontSize: '3rem', marginBottom: 'var(--space-md)' }}>üí≥</div>
              <h4 style={{ marginBottom: 'var(--space-sm)' }}>ÿØŸÅÿπ ÿ¢ŸÖŸÜ</h4>
              <p style={{ color: 'var(--gray-500)', fontSize: '0.9rem' }}>
                ÿßÿØŸÅÿπ ÿ®ÿßŸÑÿ®ÿ∑ÿßŸÇÿ© ÿ£Ÿà ŸÖÿØŸâ ÿ£Ÿà ÿ™ŸÇÿ≥Ÿäÿ∑ ÿπÿ®ÿ± ÿ™ÿßÿ®Ÿä
              </p>
            </div>
            <div className="card" style={{ textAlign: 'center', padding: 'var(--space-xl)' }}>
              <div style={{ fontSize: '3rem', marginBottom: 'var(--space-md)' }}>üõ°Ô∏è</div>
              <h4 style={{ marginBottom: 'var(--space-sm)' }}>ÿ∂ŸÖÿßŸÜ ÿ¥ÿßŸÖŸÑ</h4>
              <p style={{ color: 'var(--gray-500)', fontSize: '0.9rem' }}>
                ÿ∂ŸÖÿßŸÜ ŸäÿµŸÑ ÿ•ŸÑŸâ 5 ÿ≥ŸÜŸàÿßÿ™ ÿπŸÑŸâ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* Newsletter Section */}
      <section style={{
        background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 100%)',
        padding: 'var(--space-3xl) var(--space-lg)',
        color: 'var(--white)',
        textAlign: 'center'
      }}>
        <div className="container">
          <div style={{ maxWidth: '500px', margin: '0 auto' }}>
            <div style={{ fontSize: '3rem', marginBottom: 'var(--space-lg)' }}>üéÅ</div>
            <h2 style={{ color: 'var(--white)', marginBottom: 'var(--space-md)' }}>
              ÿßÿ≠ÿµŸÑ ÿπŸÑŸâ ÿÆÿµŸÖ 10%
