import React, { useState, useEffect } from 'react';
import { Link, useSearchParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';
import { getAllProducts, getProductsByCategory, searchProducts } from '../utils/database';
import Navbar from '../components/Navbar';

const Products = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();
  const { addToCart } = useCart();
  
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [selectedBrand, setSelectedBrand] = useState('all');
  const [sortBy, setSortBy] = useState('featured');
  const [searchQuery, setSearchQuery] = useState('');
  const [priceRange, setPriceRange] = useState({ min: 0, max: 10000 });

  const categories = [
    { id: 'all', name: 'ÿßŸÑŸÉŸÑ', icon: 'üì¶' },
    { id: 'gpu', name: 'ŸÉÿ±Ÿàÿ™ ÿßŸÑÿ¥ÿßÿ¥ÿ©', icon: 'üéÆ' },
    { id: 'cpu', name: 'ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿßÿ™', icon: '‚ö°' },
    { id: 'ram', name: 'ÿßŸÑÿ∞ÿßŸÉÿ±ÿ© RAM', icon: 'üß†' },
    { id: 'storage', name: 'ÿßŸÑÿ™ÿÆÿ≤ŸäŸÜ SSD', icon: 'üíæ' },
    { id: 'motherboard', name: 'ÿßŸÑŸÑŸàÿ≠ÿßÿ™ ÿßŸÑÿ£ŸÖ', icon: 'üîß' },
    { id: 'psu', name: 'ÿßŸÑÿ∑ÿßŸÇÿ© PSU', icon: 'üîå' },
    { id: 'cooling', name: 'ÿßŸÑÿ™ÿ®ÿ±ŸäÿØ', icon: '‚ùÑÔ∏è' },
    { id: 'case', name: 'ÿßŸÑŸÉŸäÿ≥ÿßÿ™', icon: 'üñ•Ô∏è' }
  ];

  const brands = ['all', 'NVIDIA', 'AMD', 'Intel', 'ASUS', 'MSI', 'Corsair', 'G.Skill', 'Samsung', 'NZXT', 'Lian Li'];

  // Load products
  useEffect(() => {
    const categoryFromUrl = searchParams.get('category');
    if (categoryFromUrl) {
      setSelectedCategory(categoryFromUrl);
    }
    
    const allProducts = getAllProducts();
    setProducts(allProducts);
  }, [searchParams]);

  // Filter and sort products
  useEffect(() => {
    let result = [...products];

    // Filter by category
    if (selectedCategory !== 'all') {
      result = result.filter(p => p.category === selectedCategory);
    }

    // Filter by brand
    if (selectedBrand !== 'all') {
      result = result.filter(p => p.brand === selectedBrand);
    }

    // Filter by search
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      result = result.filter(p => 
        p.name.toLowerCase().includes(query) ||
        p.nameEn.toLowerCase().includes(query) ||
        p.brand.toLowerCase().includes(query)
      );
    }

    // Filter by price
    result = result.filter(p => p.price >= priceRange.min && p.price <= priceRange.max);

    // Sort
    switch (sortBy) {
      case 'price-low':
        result.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        result.sort((a, b) => b.price - a.price);
        break;
      case 'rating':
        result.sort((a, b) => b.rating - a.rating);
        break;
      case 'newest':
        result.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        break;
      case 'featured':
      default:
        result.sort((a, b) => (b.featured ? 1 : 0) - (a.featured ? 1 : 0));
        break;
    }

    setFilteredProducts(result);
  }, [products, selectedCategory, selectedBrand, sortBy, searchQuery, priceRange]);

  const handleAddToCart = (productId) => {
    if (!isAuthenticated()) {
      navigate('/login', { state: { from: '/products' } });
      return;
    }
    addToCart(productId, 1);
  };

  const getCategoryIcon = (category) => {
    const cat = categories.find(c => c.id === category);
    return cat ? cat.icon : 'üì¶';
  };

  return (
    <div>
      <Navbar />
      
      {/* Header */}
      <section style={{
        background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 100%)',
        padding: 'var(--space-2xl) var(--space-lg)',
        color: 'var(--white)',
        textAlign: 'center'
      }}>
        <div className="container">
          <h1 style={{ marginBottom: 'var(--space-sm)', color: 'var(--white)' }}>
            üõí ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™
          </h1>
          <p style={{ opacity: 0.8 }}>
            ÿßŸÉÿ™ÿ¥ŸÅ ÿ£ÿ≠ÿØÿ´ ŸÇÿ∑ÿπ ÿßŸÑŸÉŸÖÿ®ŸäŸàÿ™ÿ± ÿ®ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿ£ÿ≥ÿπÿßÿ±
          </p>
        </div>
      </section>

      <div className="container" style={{ padding: 'var(--space-xl) var(--space-lg)' }}>
        <div style={{ display: 'grid', gridTemplateColumns: '280px 1fr', gap: 'var(--space-xl)' }}>
          
          {/* Sidebar Filters */}
          <aside>
            {/* Search */}
            <div className="card" style={{ marginBottom: 'var(--space-lg)' }}>
              <div className="card-body">
                <h4 style={{ marginBottom: 'var(--space-md)' }}>üîç ÿßŸÑÿ®ÿ≠ÿ´</h4>
                <input
                  type="text"
                  className="form-input"
                  placeholder="ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÖŸÜÿ™ÿ¨..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>
            </div>

            {/* Categories */}
            <div className="card" style={{ marginBottom: 'var(--space-lg)' }}>
              <div className="card-body">
                <h4 style={{ marginBottom: 'var(--space-md)' }}>üìÇ ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™</h4>
                <div style={{ display: 'flex', flexDirection: 'column', gap: 'var(--space-xs)' }}>
                  {categories.map(cat => (
                    <button
                      key={cat.id}
                      onClick={() => setSelectedCategory(cat.id)}
                      style={{
                        padding: 'var(--space-sm) var(--space-md)',
                        background: selectedCategory === cat.id ? 'var(--primary-lighter)' : 'transparent',
                        border: 'none',
                        borderRadius: 'var(--radius-md)',
                        textAlign: 'right',
                        cursor: 'pointer',
                        color: selectedCategory === cat.id ? 'var(--primary)' : 'var(--gray-700)',
                        fontWeight: selectedCategory === cat.id ? '600' : '400',
                        transition: 'all var(--transition-fast)'
                      }}
                    >
                      {cat.icon} {cat.name}
                    </button>
                  ))}
                </div>
              </div>
            </div>

            {/* Brands */}
            <div className="card" style={{ marginBottom: 'var(--space-lg)' }}>
              <div className="card-body">
                <h4 style={{ marginBottom: 'var(--space-md)' }}>üè∑Ô∏è ÿßŸÑŸÖÿßÿ±ŸÉÿßÿ™</h4>
                <select
                  className="form-select"
                  value={selectedBrand}
                  onChange={(e) => setSelectedBrand(e.target.value)}
                >
                  <option value="all">ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖÿßÿ±ŸÉÿßÿ™</option>
                  {brands.slice(1).map(brand => (
                    <option key={brand} value={brand}>{brand}</option>
                  ))}
                </select>
              </div>
            </div>

            {/* Price Range */}
            <div className="card">
              <div className="card-body">
                <h4 style={{ marginBottom: 'var(--space-md)' }}>üí∞ ÿßŸÑÿ≥ÿπÿ±</h4>
                <div style={{ display: 'flex', gap: 'var(--space-sm)', alignItems: 'center' }}>
                  <input
                    type="number"
                    className="form-input"
                    placeholder="ŸÖŸÜ"
                    value={priceRange.min}
                    onChange={(e) => setPriceRange(prev => ({ ...prev, min: Number(e.target.value) }))}
                    style={{ width: '100px' }}
                  />
                  <span>-</span>
                  <input
                    type="number"
                    className="form-input"
                    placeholder="ÿ•ŸÑŸâ"
                    value={priceRange.max}
                    onChange={(e) => setPriceRange(prev => ({ ...prev, max: Number(e.target.value) }))}
                    style={{ width: '100px' }}
                  />
                </div>
              </div>
            </div>
          </aside>

          {/* Products Grid */}
          <main>
            {/* Sort & Results Count */}
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              marginBottom: 'var(--space-lg)',
              flexWrap: 'wrap',
              gap: 'var(--space-md)'
            }}>
              <p style={{ color: 'var(--gray-600)' }}>
                ÿπÿ±ÿ∂ <strong>{filteredProducts.length}</strong> ŸÖŸÜÿ™ÿ¨
              </p>
              <div style={{ display: 'flex', alignItems: 'center', gap: 'var(--space-sm)' }}>
                <label>ÿ™ÿ±ÿ™Ÿäÿ® ÿ≠ÿ≥ÿ®:</label>
                <select
                  className="form-select"
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  style={{ width: 'auto' }}
                >
                  <option value="featured">ÿßŸÑŸÖŸÖŸäÿ≤ÿ©</option>
                  <option value="price-low">ÿßŸÑÿ≥ÿπÿ±: ÿßŸÑÿ£ŸÇŸÑ</option>
                  <option value="price-high">ÿßŸÑÿ≥ÿπÿ±: ÿßŸÑÿ£ÿπŸÑŸâ</option>
                  <option value="rating">ÿßŸÑÿ™ŸÇŸäŸäŸÖ</option>
                  <option value="newest">ÿßŸÑÿ£ÿ≠ÿØÿ´</option>
                </select>
              </div>
            </div>

            {/* Products */}
            {filteredProducts.length === 0 ? (
              <div style={{ 
                textAlign: 'center', 
                padding: 'var(--space-3xl)',
                background: 'var(--gray-50)',
                borderRadius: 'var(--radius-lg)'
              }}>
                <div style={{ fontSize: '4rem', marginBottom: 'var(--space-lg)' }}>üîç</div>
                <h3 style={{ marginBottom: 'var(--space-sm)' }}>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖŸÜÿ™ÿ¨ÿßÿ™</h3>
                <p style={{ color: 'var(--gray-500)' }}>ÿ¨ÿ±ÿ® ÿ™ÿ∫ŸäŸäÿ± ŸÖÿπÿßŸäŸäÿ± ÿßŸÑÿ®ÿ≠ÿ´</p>
              </div>
            ) : (
              <div className="products-grid">
                {filteredProducts.map(product => (
                  <div key={product.id} className="product-card">
                    <div className="product-image" style={{
                      background: 'linear-gradient(135deg, #1a1a2e 0%, #2d2d44 100%)',
                      color: '#00ff88'
                    }}>
                      {getCategoryIcon(product.category)}
                      {product.featured && (
                        <span style={{
                          position: 'absolute',
                          top: '10px',
                          right: '10px',
                          background: '#ff6b6b',
                          color: 'white',
                          padding: '4px 8px',
                          borderRadius: 'var(--radius-sm)',
                          fontSize: '0.7rem',
                          fontWeight: '600'
                        }}>
                          ŸÖŸÖŸäÿ≤ üî•
                        </span>
                      )}
                    </div>
                    <div className="product-content">
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 'var(--space-sm)' }}>
                        <span className="product-category">
                          {categories.find(c => c.id === product.category)?.name || product.category}
                        </span>
                        <span style={{ fontSize: '0.75rem', color: 'var(--gray-500)', fontWeight: '600' }}>
                          {product.brand}
                        </span>
                      </div>
                      <h3 className="product-title">{product.name}</h3>
                      <div className="product-rating">
                        <span>‚≠ê</span>
                        <span>{product.rating}</span>
                        <span style={{ color: 'var(--gray-400)' }}>
                          ({product.reviewCount})
                        </span>
                      </div>
                      <div style={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        alignItems: 'center',
                        marginTop: 'var(--space-sm)'
                      }}>
                        <p className="product-price" style={{ margin: 0 }}>
                          <span className="product-price-currency">ÿ±.ÿ≥</span>
                          {product.price.toLocaleString()}
                        </p>
                        <span style={{
                          fontSize: '0.75rem',
                          color: product.stock > 10 ? 'var(--success)' : product.stock > 0 ? 'var(--warning)' : 'var(--error)'
                        }}>
                          {product.stock > 10 ? '‚úì ŸÖÿ™ŸàŸÅÿ±' : product.stock > 0 ? `‚ö†Ô∏è ${product.stock} ŸÅŸÇÿ∑` : '‚úó ŸÜŸÅÿ∞'}
                        </span>
                      </div>
                      <div className="product-actions">
                        <Link 
                          to={`/products/${product.id}`} 
                          className="btn btn-secondary"
                          style={{ flex: 1 }}
                        >
                          ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ
                        </Link>
                        <button 
                          className="btn btn-primary"
                          style={{ flex: 1 }}
                          onClick={() => handleAddToCart(product.id)}
                          disabled={product.stock === 0}
                        >
                          üõí ÿ£ÿ∂ŸÅ
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </main>
        </div>
      </div>
    </div>
  );
};

export default Products;
